public class TimeConstant {
    private TimeConstant() {}
    /**
     * 1天的毫秒
     */
    public static final int DAY_OF_MILLISECOND = 24 * 60 * 60 * 1000;
    /**
     * 7天的秒
     */
    public static final int WEEK_OF_SECOND = 7 * 24 * 60 * 60;
    /**
     * 1天的秒
     */
    public static final int DAY_OF_SECOND = 24 * 60 * 60;
    /**
     * 30分的毫秒
     */
    public static final int HALF_HOUR_OF_MILLISECOND = 30 * 60 * 1000;
    /**
     * 30分的秒
     */
    public static final int HALF_HOUR_OF_SECOND = 30 * 60;
    /**
     * 5分的秒
     */
    public static final int FIVE_MINUTE_OF_SECOND = 5 * 60;
    /**
     * 1分的秒
     */
    public static final int ONE_MINUTE_OF_SECOND = 60;
}
